//   paginationBtnActive: { padding: "8px 10px", borderRadius: 8, background: "#007bff", color: "white", border: "1px solid #0062cc", cursor: "pointer" },
// };
import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import "./PriceList.css";

const API_URL = "https://grocerrybackend.vercel.app/api/prices";
const CATEGORY_URL = "https://grocerrybackend.vercel.app/api/categories";

export default function PriceList() {
  const [items, setItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);

  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  const [form, setForm] = useState({
    name: "",
    category: "",
    subcategory: "",
    description: "",
    basePrice: "",
    profitLoss: "",
    validTill: "",
    file: null,
    status: "inactive",
  });

  const [editId, setEditId] = useState(null);
  const [isCopyMode, setIsCopyMode] = useState(false);
  const [showForm, setShowForm] = useState(false);

  const [selectedItems, setSelectedItems] = useState([]);
  const [bulkMode, setBulkMode] = useState(false);

  const [csvFile, setCsvFile] = useState(null);
  const csvInputRef = useRef();

  const [activeMenu, setActiveMenu] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);

  const itemsPerPage = 30;
  const isMobile = window.innerWidth < 768;

  const [quickEdit, setQuickEdit] = useState({});

  const normalizeItem = (i) => ({
    ...i,
    basePrice: Number(i.basePrice ?? 0),
    profitLoss: Number(i.profitLoss ?? 0),
    salePrice: Number(i.salePrice ?? 0),
    lockedPrice: Number(i.lockedPrice ?? 0),
    yesterdayLock: Number(i.yesterdayLock ?? 0),
    brokerDisplay: Number(i.brokerDisplay ?? 0),
  });

  // Fetch categories
  useEffect(() => {
    axios.get(CATEGORY_URL).then((res) => {
      if (res.data.success) setCategories(res.data.categories);
    });
  }, []);

  // Fetch items
  useEffect(() => {
    fetchItems();
  }, [categories]);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const res = await axios.get(API_URL);
      if (res.data.success) {
        setItems(res.data.data.map(normalizeItem));
      }
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files) setForm((p) => ({ ...p, file: files[0] }));
    else setForm((p) => ({ ...p, [name]: value }));
  };

  const resetForm = () => {
    setForm({
      name: "",
      category: "",
      subcategory: "",
      description: "",
      basePrice: "",
      profitLoss: "",
      validTill: "",
      file: null,
      status: "inactive",
    });
    setEditId(null);
    setIsCopyMode(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!form.name || !form.category || !form.basePrice) {
      alert("Missing fields");
      return;
    }

    const fd = new FormData();
    Object.keys(form).forEach((k) => {
      if (form[k] !== "" && form[k] !== null) fd.append(k, form[k]);
    });

    let res;
    if (editId && !isCopyMode)
      res = await axios.put(`${API_URL}/${editId}`, fd);
    else 
      res = await axios.post(API_URL, fd);

    if (res.data.success) {
      fetchItems();
      resetForm();
      setShowForm(false);
    }
  };

  const handleEdit = (item) => {
    setForm({
      name: item.name,
      category: item.category?._id || "",
      subcategory: item.subcategory?._id || "",
      description: item.description,
      basePrice: item.basePrice,
      profitLoss: item.profitLoss,
      validTill: item.validTill ? item.validTill.split("T")[0] : "",
      file: null,
      status: item.status,
    });
    setEditId(item._id);
    setIsCopyMode(false);
    setShowForm(true);
  };

  const handleCopy = (item) => {
    setForm({
      name: item.name,
      category: item.category?._id || "",
      subcategory: item.subcategory?._id || "",
      description: item.description,
      basePrice: item.basePrice,
      profitLoss: item.profitLoss,
      validTill: "",
      file: null,
      status: item.status,
    });
    setEditId(null);
    setIsCopyMode(true);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete item?")) return;
    await axios.delete(`${API_URL}/${id}`);
    fetchItems();
  };

  const toggleStatus = async (item) => {
    const newStatus = item.status === "active" ? "inactive" : "active";
    await axios.put(`${API_URL}/status/${item._id}`, { status: newStatus });
    fetchItems();
  };

  const updateLocalItemField = (id, key, value) => {
    setItems((prev) =>
      prev.map((x) => (x._id === id ? { ...x, [key]: value } : x))
    );
  };

  const applyQuickBase = async (item) => {
    const newBase = Number(quickEdit[item._id]);
    if (isNaN(newBase)) return alert("Invalid");
    const fd = new FormData();
    fd.append("basePrice", newBase);
    fd.append("profitLoss", item.profitLoss);

    await axios.put(`${API_URL}/${item._id}`, fd);
    fetchItems();
  };

  const getBrokerDiff = (i) => {
    const yesterday = i.yesterdayLock || 0;
    const today = i.lockedPrice || i.salePrice || 0;
    return today - yesterday;
  };

  // CSV Export All
  const exportCsv = () => {
    if (!items.length) return alert("No data to export");

    const header = [
      "id","name","category","subcategory","basePrice","profitLoss",
      "salePrice","lockedPrice","yesterdayLock","brokerDisplay","status","description","image"
    ];
    
    const rows = items.map(i => [
      i._id, i.name, i.category?.name || "", i.subcategory?.name || "",
      i.basePrice, i.profitLoss, i.salePrice, i.lockedPrice,
      i.yesterdayLock, i.brokerDisplay, i.status, i.description, i.image
    ]);

    const csv = [header, ...rows]
      .map(r => r.map(c => `"${c}"`).join(","))
      .join("\n");

    const a = document.createElement("a");
    a.href = "data:text/csv;charset=utf-8," + encodeURI(csv);
    a.download = "products.csv";
    a.click();
  };

  const filtered = items.filter((i) =>
    i.name.toLowerCase().includes(search.toLowerCase())
  );

  const start = (currentPage - 1) * itemsPerPage;
  const currentItems = filtered.slice(start, start + itemsPerPage);
  const totalPages = Math.ceil(filtered.length / itemsPerPage);

  return (
    <div style={{ padding: 20 }}>
      <h1 style={{ fontSize: 22, fontWeight: 700 }}>Product Management</h1>

      {/* SEARCH + ADD */}
      <div style={{ display: "flex", gap: 10, marginBottom: 15 }}>
        <input
          placeholder="Search..."
          value={search}
          onChange={(e) => {
            setSearch(e.target.value);
            setCurrentPage(1);
          }}
          style={{
            padding: 10,
            width: 280,
            borderRadius: 8,
            border: "1px solid #ccc",
          }}
        />

        <button
          onClick={() => {
            setShowForm(!showForm);
            resetForm();
          }}
          style={{
            background: "#007bff",
            color: "#fff",
            borderRadius: 8,
            padding: "10px 14px",
            border: "none",
            cursor: "pointer",
          }}
        >
          {showForm ? "Close" : "Add Product"}
        </button>

        <button
          onClick={exportCsv}
          style={{
            background: "#28a745",
            color: "#fff",
            borderRadius: 8,
            padding: "10px 14px",
            border: "none",
            marginLeft: "auto",
            cursor: "pointer",
          }}
        >
          Export CSV
        </button>
      </div>

      {/* FORM */}
      {showForm && (
        <form
          onSubmit={handleSubmit}
          style={{
            padding: 15,
            background: "#fff",
            borderRadius: 10,
            border: "1px solid #ddd",
            marginBottom: 20,
          }}
        >
          <div style={{ display: "flex", gap: 15, flexWrap: "wrap" }}>
            <div style={{ flex: "1 1 200px" }}>
              <label>Name *</label>
              <input
                name="name"
                value={form.name}
                onChange={handleChange}
                required
                style={inputStyle}
              />
            </div>

            <div style={{ flex: "1 1 200px" }}>
              <label>Category *</label>
              <select
                name="category"
                value={form.category}
                onChange={(e) => {
                  handleChange(e);
                  setSubcategories([]);
                }}
                required
                style={inputStyle}
              >
                <option value="">Select</option>
                {categories.map((c) => (
                  <option value={c._id} key={c._id}>
                    {c.name}
                  </option>
                ))}
              </select>
            </div>

            <div style={{ flex: "1 1 200px" }}>
              <label>Base Price *</label>
              <input
                type="number"
                name="basePrice"
                value={form.basePrice}
                onChange={handleChange}
                required
                style={inputStyle}
              />
            </div>

            <div style={{ flex: "1 1 200px" }}>
              <label>Profit/Loss</label>
              <input
                type="number"
                name="profitLoss"
                value={form.profitLoss}
                onChange={handleChange}
                style={inputStyle}
              />
            </div>

            <div style={{ flex: "1 1 200px" }}>
              <label>Image</label>
              <input type="file" onChange={handleChange} />
            </div>
          </div>

          <label>Description</label>
          <textarea
            name="description"
            value={form.description}
            onChange={handleChange}
            style={{ ...inputStyle, height: 80 }}
          />

          <button
            type="submit"
            style={{
              background: "#007bff",
              color: "#fff",
              padding: "10px 16px",
              borderRadius: 8,
              border: "none",
              marginTop: 10,
              cursor: "pointer",
            }}
          >
            {editId ? "Update Product" : "Add Product"}
          </button>
        </form>
      )}

      {/* TABLE */}
      <div
        style={{
          border: "1px solid #ddd",
          borderRadius: 8,
          overflowX: "auto",
          background: "#fff",
        }}
      >
        <table style={{ width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ background: "#f1f1f1" }}>
              <th style={th}>Sr</th>
              <th style={th}>Image</th>
              <th style={th}>Name</th>
              <th style={th}>Category</th>
              <th style={th}>Base</th>
              <th style={th}>P/L</th>
              <th style={th}>Sale</th>
              <th style={th}>Lock</th>
              <th style={th}>Tejji/Mandi</th>
              <th style={th}>Status</th>
              <th style={th}>Actions</th>
            </tr>
          </thead>

          <tbody>
            {currentItems.map((item, i) => (
              <tr key={item._id} style={{ borderBottom: "1px solid #eee" }}>
                <td style={td}>{start + i + 1}</td>

                <td style={td}>
                  {item.image ? (
                    <img
                      src={item.image}
                      style={{ width: 45, height: 45, borderRadius: 6 }}
                      alt=""
                    />
                  ) : (
                    "No Img"
                  )}
                </td>

                <td style={td}>{item.name}</td>
                <td style={td}>{item.category?.name}</td>

                <td style={td}>
                  <input
                    type="number"
                    value={
                      quickEdit[item._id] !== undefined
                        ? quickEdit[item._id]
                        : item.basePrice
                    }
                    onChange={(e) =>
                      setQuickEdit((p) => ({
                        ...p,
                        [item._id]: e.target.value,
                      }))
                    }
                    style={{ width: 70 }}
                  />
                  <button
                    style={smallBtn}
                    onClick={() => applyQuickBase(item)}
                  >
                    ‚úî
                  </button>
                </td>

                <td style={td}>
                  <input
                    type="number"
                    value={item.profitLoss}
                    onChange={(e) =>
                      updateLocalItemField(
                        item._id,
                        "profitLoss",
                        Number(e.target.value)
                      )
                    }
                    style={{ width: 70 }}
                  />
                </td>

                <td style={td}>{item.salePrice}</td>
                <td style={td}>{item.lockedPrice}</td>

                <td style={td}>{getBrokerDiff(item)}</td>

                <td style={td}>
                  <button
                    onClick={() => toggleStatus(item)}
                    style={{
                      background:
                        item.status === "active" ? "#29b65f" : "#ccc",
                      color: "#fff",
                      borderRadius: 6,
                      border: "none",
                      padding: "6px 10px",
                      cursor: "pointer",
                    }}
                  >
                    {item.status}
                  </button>
                </td>

                <td style={td}>
                  <button style={smallBtn} onClick={() => handleEdit(item)}>
                    ‚úè
                  </button>
                  <button style={smallBtn} onClick={() => handleCopy(item)}>
                    üìÑ
                  </button>
                  <button
                    style={{ ...smallBtn, background: "red" }}
                    onClick={() => handleDelete(item._id)}
                  >
                    üóë
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      <div style={{ marginTop: 15, display: "flex", gap: 10 }}>
        <button
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((p) => p - 1)}
        >
          Previous
        </button>

        {Array.from({ length: totalPages }, (_, i) => (
          <button
            key={i}
            onClick={() => setCurrentPage(i + 1)}
            style={{
              background: currentPage === i + 1 ? "#007bff" : "#fff",
              color: currentPage === i + 1 ? "#fff" : "#000",
            }}
          >
            {i + 1}
          </button>
        ))}

        <button
          disabled={currentPage === totalPages}
          onClick={() => setCurrentPage((p) => p + 1)}
        >
          Next
        </button>
      </div>
    </div>
  );
}

const inputStyle = {
  width: "100%",
  padding: 8,
  borderRadius: 6,
  border: "1px solid #ccc",
  marginTop: 4,
};

const th = {
  padding: 10,
  fontWeight: 700,
  fontSize: 14,
};

const td = {
  padding: 10,
};

const smallBtn = {
  padding: "5px 9px",
  background: "#007bff",
  color: "#fff",
  borderRadius: 6,
  border: "none",
  marginLeft: 6,
  cursor: "pointer",
};
