// PriceList.jsx - PART 1: imports, state, hooks, API helpers, functions

import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import "./PriceList.css"; // keep your CSS file; styles also included in PART 3 fallback

const API_URL = "https://grocerrybackend.vercel.app/api/prices";
const CATEGORY_URL = "https://grocerrybackend.vercel.app/api/categories";

export default function PriceList() {
  // ---------------------- STATE ----------------------
  const [items, setItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);

  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  // Form for add/edit
  const [form, setForm] = useState({
    name: "",
    category: "",
    subcategory: "",
    description: "",
    basePrice: "",
    profitLoss: "",
    validTill: "",
    file: null,
    status: "inactive",
  });

  const [editId, setEditId] = useState(null);
  const [isCopyMode, setIsCopyMode] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const [selectedItems, setSelectedItems] = useState([]);
  const [bulkMode, setBulkMode] = useState(false);

  const [csvFile, setCsvFile] = useState(null);
  const csvInputRef = useRef();

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 30;

  const [filterCategory, setFilterCategory] = useState("");
  const [filterSubcategory, setFilterSubcategory] = useState("");
  const [filterSubs, setFilterSubs] = useState([]);

  const [quickEdit, setQuickEdit] = useState({});
  const [activeMenu, setActiveMenu] = useState(null);

  // mobile detection
  const [isMobile, setIsMobile] = useState(
    typeof window !== "undefined" ? window.innerWidth < 768 : false
  );

  // ---------------------- HELPERS ----------------------
  const normalizeItem = (item) => {
    // ensure numeric fields are numbers and exist
    return {
      ...item,
      basePrice: Number(item.basePrice ?? 0),
      profitLoss: Number(item.profitLoss ?? 0),
      salePrice: Number(item.salePrice ?? item.basePrice ?? 0),
      lockedPrice: Number(item.lockedPrice ?? 0),
      yesterdayLock: Number(item.yesterdayLock ?? 0),
      brokerDisplay: Number(item.brokerDisplay ?? 0),
    };
  };

  // ---------------------- API: Categories / Items ----------------------
  const fetchCategories = async () => {
    try {
      const res = await axios.get(CATEGORY_URL);
      if (res.data && res.data.success) {
        setCategories(res.data.categories || []);
      }
    } catch (err) {
      console.error("fetchCategories:", err);
      alert("Could not load categories");
    }
  };

  const fetchItems = async () => {
    try {
      setLoading(true);
      const res = await axios.get(API_URL);
      if (res.data && res.data.success) {
        const data = (res.data.data || []).map((x) => {
          // hydrate category/subcategory objects (if server returns id only)
          const catId = x.category?._id || x.category;
          const catObj = categories.find((c) => String(c._id) === String(catId));
          let subObj = null;
          if (x.subcategory) {
            if (typeof x.subcategory === "object") {
              subObj = {
                _id: x.subcategory._id || "",
                name: x.subcategory.name || "",
              };
            } else if (catObj && Array.isArray(catObj.subcategories)) {
              subObj =
                catObj.subcategories.find(
                  (s) =>
                    String(s._id) === String(x.subcategory) ||
                    (s.name && s.name.toLowerCase() === String(x.subcategory).toLowerCase())
                ) || { _id: "", name: x.subcategory };
            } else {
              subObj = { _id: "", name: x.subcategory };
            }
          }
          return normalizeItem({
            ...x,
            category: catObj ? { _id: catObj._id, name: catObj.name } : x.category,
            subcategory: subObj,
          });
        });
        setItems(data);
      }
    } catch (err) {
      console.error("fetchItems:", err);
      alert("Could not load items");
    } finally {
      setLoading(false);
    }
  };

  // ---------------------- UI Effects ----------------------
  useEffect(() => {
    fetchCategories();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // fetch items after categories available (so subcategory mapping works)
  useEffect(() => {
    if (categories.length > 0) fetchItems();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [categories]);

  // responsive resize listener
  useEffect(() => {
    const onResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  // close dropdown when clicking outside
  useEffect(() => {
    const onDoc = (e) => {
      if (!e.target.closest) return;
      const el = e.target.closest(".price-row-menu");
      if (!el) setActiveMenu(null);
    };
    document.addEventListener("click", onDoc);
    return () => document.removeEventListener("click", onDoc);
  }, []);

  // update filterSubs whenever filterCategory changes
  useEffect(() => {
    if (!filterCategory) {
      setFilterSubs([]);
      setFilterSubcategory("");
      return;
    }
    const cat = categories.find((c) => String(c._id) === String(filterCategory));
    setFilterSubs(cat?.subcategories || []);
    setFilterSubcategory("");
  }, [filterCategory, categories]);

  // update subcategories when form.category changes
  useEffect(() => {
    if (!form.category) {
      setSubcategories([]);
      setForm((p) => ({ ...p, subcategory: "" }));
      return;
    }
    const cat = categories.find((c) => String(c._id) === String(form.category));
    setSubcategories(cat?.subcategories || []);
    const exists = cat?.subcategories?.some((s) => String(s._id) === String(form.subcategory));
    if (!exists) setForm((p) => ({ ...p, subcategory: "" }));
  }, [form.category, categories]);

  // ---------------------- FORM HANDLERS ----------------------
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files && files.length) {
      setForm((p) => ({ ...p, file: files[0] }));
    } else {
      setForm((p) => ({ ...p, [name]: value }));
    }
  };

  const resetForm = () => {
    setForm({
      name: "",
      category: "",
      subcategory: "",
      description: "",
      basePrice: "",
      profitLoss: "",
      validTill: "",
      file: null,
      status: "inactive",
    });
    setEditId(null);
    setIsCopyMode(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name || !form.category || form.basePrice === "") {
      alert("Name, category & base price are required");
      return;
    }

    try {
      setLoading(true);
      const fd = new FormData();
      Object.keys(form).forEach((k) => {
        const val = form[k];
        if (val !== undefined && val !== null && val !== "") fd.append(k, val);
      });

      let res;
      if (isCopyMode) {
        res = await axios.post(API_URL, fd);
      } else if (editId) {
        res = await axios.put(`${API_URL}/${editId}`, fd);
      } else {
        res = await axios.post(API_URL, fd);
      }

      if (res.data && res.data.success) {
        await fetchItems();
        await fetchCategories();
        resetForm();
        setShowForm(false);
        setShowModal(false);
      } else {
        alert("Save failed");
      }
    } catch (err) {
      console.error("handleSubmit:", err);
      alert("Save failed");
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (item) => {
    setForm({
      name: item.name || "",
      category: item.category?._id || (item.category || ""),
      subcategory: item.subcategory?._id || (item.subcategory || ""),
      description: item.description || "",
      basePrice: item.basePrice ?? "",
      profitLoss: item.profitLoss ?? 0,
      validTill: item.validTill ? item.validTill.split("T")[0] : "",
      file: null,
      status: item.status || "inactive",
    });
    setEditId(item._id);
    setIsCopyMode(false);
    setShowModal(true);
  };

  const handleCopyToEdit = (item) => {
    setForm({
      name: item.name || "",
      category: item.category?._id || (item.category || ""),
      subcategory: item.subcategory?._id || (item.subcategory || ""),
      description: item.description || "",
      basePrice: item.basePrice ?? "",
      profitLoss: item.profitLoss ?? 0,
      validTill: item.validTill ? item.validTill.split("T")[0] : "",
      file: null,
      status: item.status || "inactive",
    });
    setEditId(null);
    setIsCopyMode(true);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Delete item?")) return;
    try {
      await axios.delete(`${API_URL}/${id}`);
      await fetchItems();
      setSelectedItems((s) => s.filter((x) => x !== id));
    } catch (err) {
      console.error("handleDelete:", err);
      alert("Delete failed");
    }
  };

  const handleStatusToggle = async (item) => {
    try {
      const newStatus = item.status === "active" ? "inactive" : "active";
      const res = await axios.put(`${API_URL}/status/${item._id}`, { status: newStatus });
      if (res.data && res.data.success) await fetchItems();
    } catch (err) {
      console.error("handleStatusToggle:", err);
      alert("Status update failed");
    }
  };

  // ---------------------- CSV (select/import/export) ----------------------
  const handleCsvSelect = (e) => setCsvFile(e.target.files?.[0] ?? null);

  const handleImportCsv = async () => {
    if (!csvFile) return alert("Select CSV file");
    try {
      const fd = new FormData();
      fd.append("file", csvFile);
      const res = await axios.post(`${API_URL}/import`, fd);
      if (res.data && res.data.success) {
        alert(`Imported ${res.data.inserted || 0} items`);
        setCsvFile(null);
        if (csvInputRef.current) csvInputRef.current.value = null;
        await fetchItems();
      } else {
        alert("CSV import failed");
      }
    } catch (err) {
      console.error("handleImportCsv:", err);
      alert("CSV import failed");
    }
  };

  // NOTE: export functions implemented in PART 2/3 (UI) will call these helpers
  const exportCsvFromItems = (rows, filename) => {
    const csvArray = rows;
    const csvContent =
      "data:text/csv;charset=utf-8," +
      csvArray.map((r) => r.map((c) => `"${String(c).replace(/"/g, '""')}"`).join(",")).join("\n");
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.href = encodedUri;
    link.download = `${filename}_${Date.now()}.csv`;
    link.click();
  };

  // ---------------------- BULK ----------------------
  const handleBulkDelete = async () => {
    if (!selectedItems.length) return alert("No items selected");
    if (!window.confirm("Delete selected items?")) return;
    try {
      await axios.post(`${API_URL}/delete-selected`, { ids: selectedItems });
      setSelectedItems([]);
      setBulkMode(false);
      await fetchItems();
    } catch (err) {
      console.error("handleBulkDelete:", err);
      alert("Bulk delete failed");
    }
  };

  const handleBulkSave = async () => {
    if (!selectedItems.length) return alert("No items selected");
    const updates = items
      .filter((x) => selectedItems.includes(x._id))
      .map((x) => ({
        id: x._id,
        basePrice: Number(x.basePrice),
        profitLoss: Number(x.profitLoss),
        status: x.status,
        validTill: x.validTill || null,
        name: x.name,
        category: x.category?._id || "",
        subcategory: x.subcategory?._id || "",
      }));
    try {
      const res = await axios.post(`${API_URL}/bulk-update`, { products: updates });
      if (res.data && res.data.success) {
        alert("Bulk Updated");
        setSelectedItems([]);
        setBulkMode(false);
        await fetchItems();
      } else {
        alert("Bulk update failed");
      }
    } catch (err) {
      console.error("handleBulkSave:", err);
      alert("Bulk update failed");
    }
  };

  // ---------------------- QUICK BASE APPLY (per-row) ----------------------
  const applyQuickBase = async (item) => {
    const newBase = Number(quickEdit[item._id]);
    if (isNaN(newBase)) return alert("Invalid base price");
    try {
      setLoading(true);
      const fd = new FormData();
      fd.append("basePrice", newBase);
      fd.append("profitLoss", item.profitLoss ?? 0);
      const res = await axios.put(`${API_URL}/${item._id}`, fd);
      if (res.data && res.data.success) {
        await fetchItems();
        setQuickEdit((p) => ({ ...p, [item._id]: newBase }));
      } else {
        alert("Base update failed");
      }
    } catch (err) {
      console.error("applyQuickBase:", err);
      alert("Base update failed");
    } finally {
      setLoading(false);
    }
  };

  // ---------------------- INLINE LOCAL UPDATE HELPER ----------------------
  // used by inputs that change values locally before bulk save
  const updateLocalItemField = (id, key, value) => {
    setItems((prev) => prev.map((x) => (x._id === id ? { ...x, [key]: value } : x)));
  };

  // Attach to window for PART 2/3 compatibility if needed (not required)
  // window.__updateLocal = updateLocalItemField;

  // ---------------------- PRICE HELPERS ----------------------
  const getFinalPrice = (item) => {
    return Number(item.salePrice ?? item.basePrice ?? 0);
  };

  const getBrokerDiff = (item) => {
    const yesterday = Number(item.yesterdayLock ?? 0);
    const today = Number(item.lockedPrice ?? item.salePrice ?? 0);
    return Number(today - yesterday);
  };

  // ---------------------- MENU ----------------------
  const toggleMenu = (id) => {
    setActiveMenu((prev) => (prev === id ? null : id));
  };

  // ---------------------- PART 1 END ----------------------
  // The JSX render (return ...) continues in PART 2 + PART 3
  // Make sure to paste PART 2 then PART 3 after this block inside same file.
}
