const fetchItems = async () => {
  try {
    setLoading(true);
    const res = await axios.get(API_URL);
    if (res.data && res.data.success) {
      const raw = res.data.data || [];

      // Enrich each item using categories currently loaded (safe if category is id or object)
      const enriched = raw.map((item) => {
        // category may be populated object, or id string
        let catObj = null;
        const catId = item.category && (typeof item.category === "object" ? item.category._id : item.category);

        if (catId) {
          catObj = categories.find((c) => String(c._id) === String(catId)) || null;
        } else if (item.category && typeof item.category === "object") {
          catObj = { _id: item.category._id, name: item.category.name, subcategories: item.category.subcategories || [] };
        }

        // subcategory may be object or string; try to find by id/name inside category
        let subObj = null;
        if (item.subcategory && typeof item.subcategory === "object") {
          subObj = { _id: item.subcategory._id, name: item.subcategory.name };
        } else if (catObj && catObj.subcategories) {
          // item.subcategory might be id or name
          const subIdOrName = item.subcategory;
          subObj = catObj.subcategories.find(
            (s) => String(s._id) === String(subIdOrName) || String(s.name).toLowerCase() === String(subIdOrName).toLowerCase()
          );
        }

        return {
          ...item,
          todayDiff: Number(item.todayDiff ?? 0),
          lastFinalPrice: Number(item.lastFinalPrice ?? item.basePrice ?? 0),
          currentFinalPrice: Number(item.currentFinalPrice ?? 0),
          category: catObj ? { _id: catObj._id, name: catObj.name } : null,
          subcategory: subObj ? { _id: subObj._id, name: subObj.name } : null,
        };
      });

      setItems(enriched);
    }
  } catch (err) {
    console.error("fetchItems error", err);
    alert("Could not fetch items");
  } finally {
    setLoading(false);
  }
};

