                      accept="image/*"
                      onChange={(e) => {
                        setNewCategoryImage(e.target.files[0]);
                        setNewCategoryPreview(e.target.files[0] ? URL.createObjectURL(e.target.files[0]) : null);
                      }}
                      style={styles.fileInput}
                    />
