import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import ChartDataLabels from "chartjs-plugin-datalabels";
import { Line } from "react-chartjs-2";
import "./PriceAnalytics.css";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ChartDataLabels
);

const API_BASE = "https://grocerrybackend.vercel.app/api";

const PriceAnalytics = () => {
  const [loading, setLoading] = useState(true);
  const [productList, setProductList] = useState([]);
  const [categoryList, setCategoryList] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchDropdowns = async () => {
      try {
        const [p, c] = await Promise.all([
          axios.get(`${API_BASE}/prices`),
          axios.get(`${API_BASE}/categories`),
        ]);

        setProductList(p.data.data || []);
        setCategoryList(c.data.categories || []);
      } catch (err) {
        console.error("Dropdown fetch error:", err);
      }
    };

    fetchDropdowns();
  }, []);

  const fetchChart = async () => {
    setLoading(true);
    try {
      const res = await axios.get(`${API_BASE}/prices`);
      let all = res.data.data || [];

      // Filters
      if (selectedProduct)
        all = all.filter((p) => p.name === selectedProduct);

      if (selectedCategory)
        all = all.filter((p) => p.category?.name === selectedCategory);

      // Group by Date
      const grouped = {};
      all.forEach((item) => {
        const k = new Date(item.validTill || item.createdAt).toLocaleDateString("en-IN");
        if (!grouped[k]) grouped[k] = [];
        grouped[k].push(item);
      });

      const labels = Object.keys(grouped);

      // Final Price
      const finalPrice = labels.map((d) => {
        const arr = grouped[d];
        return arr.reduce(
          (sum, x) => sum + (Number(x.basePrice || 0) + Number(x.difference || 0)),
          0
        ) / arr.length;
      });

      setChartData({
        labels,
        datasets: [
          {
            label: "Final Price (‚Çπ)",
            data: finalPrice,
            borderColor: "#007bff",
            backgroundColor: "rgba(0,123,255,0.3)",
            tension: 0.3,
          },
        ],
      });
    } catch (err) {
      console.error("Chart error:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchChart();
  }, [selectedProduct, selectedCategory]);

  return (
    <div className="analytics-container">
      <h2 className="analytics-title">üìä Final Price Analytics</h2>

      {/* Filters */}
      <div className="filter-section">
        <select value={selectedProduct} onChange={(e) => setSelectedProduct(e.target.value)}>
          <option value="">All Products</option>
          {productList.map((p) => (
            <option key={p._id} value={p.name}>{p.name}</option>
          ))}
        </select>

        <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
          <option value="">All Categories</option>
          {categoryList.map((c) => (
            <option key={c._id} value={c.name}>{c.name}</option>
          ))}
        </select>

        <button className="btn refresh" onClick={fetchChart}>Apply</button>
      </div>

      {/* Chart */}
      <div className="chart-wrapper">
        {loading ? (
          <div className="loading">Loading...</div>
        ) : (
          <Line
            data={chartData}
            options={{
              responsive: true,
              maintainAspectRatio: false,
              plugins: {
