function updateLocalItemField(id, key, value) {
  // This function will be hoisted to window scope if defined outside; to keep inside component,
  // we defined it by referencing state updater in part1. But since we are sending single-file component,
  // ensure this helper exists in the top of the file. If you paste both parts in same file, replace this with
  // the inline version used earlier:
  // setItems(prev => prev.map(x => x._id === id ? { ...x, [key]: value } : x));
  // For safety, we attach to window to fallback:
  if (typeof window.__updateLocal === "function") {
    window.__updateLocal(id, key, value);
  } else {
    console.warn("updateLocalItemField helper missing ‚Äî please ensure you paste PART 1 then PART 2 in same file so the internal updater is available.");
  }
}

