import React, { useEffect, useState, useRef } from "react";
import axios from "axios";
import "./PriceList.css";

// NEW BACKEND URL
const API_URL = "https://grocerrybackend.vercel.app/api/prices";
const CATEGORY_URL = "https://grocerrybackend.vercel.app/api/categories";

export default function PriceList() {
  const [items, setItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);

  const [search, setSearch] = useState("");
  const [loading, setLoading] = useState(false);

  // FORM for Add/Edit
  const [form, setForm] = useState({
    name: "",
    category: "",
    subcategory: "",
    description: "",
    basePrice: "",
    profitLoss: "",      // UPDATED FIELD (instead of difference)
    validTill: "",
    file: null,
    status: "inactive",
  });

  const [editId, setEditId] = useState(null);
  const [isCopyMode, setIsCopyMode] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [showModal, setShowModal] = useState(false);

  const [selectedItems, setSelectedItems] = useState([]);
  const [bulkMode, setBulkMode] = useState(false);

  const [csvFile, setCsvFile] = useState(null);
  const csvInputRef = useRef();

  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 30;

  const [filterCategory, setFilterCategory] = useState("");
  const [filterSubcategory, setFilterSubcategory] = useState("");
  const [filterSubs, setFilterSubs] = useState([]);

  // NEW ‚Äî Quick Edit Base & ProfitLoss
  const [quickEdit, setQuickEdit] = useState({});

  // NORMALIZE ITEM ACCORDING TO NEW BACKEND FIELDS
  const normalizeItem = (item) => {
    return {
      ...item,
      basePrice: Number(item.basePrice ?? 0),
      profitLoss: Number(item.profitLoss ?? 0),
      salePrice: Number(item.salePrice ?? 0),
      lockedPrice: Number(item.lockedPrice ?? 0),
      yesterdayLock: Number(item.yesterdayLock ?? 0),
      brokerDisplay: Number(item.brokerDisplay ?? 0),
    };
  };

  // FETCH CATEGORIES
  useEffect(() => {
    fetchCategories();
  }, []);

  const fetchCategories = async () => {
    try {
      const res = await axios.get(CATEGORY_URL);
      if (res.data.success) setCategories(res.data.categories || []);
    } catch (err) {
      alert("Categories load error");
    }
  };

  // FETCH ITEMS
  useEffect(() => {
    if (categories.length > 0) fetchItems();
  }, [categories]);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const res = await axios.get(API_URL);

      if (res.data.success) {
        const data = res.data.data.map((x) => normalizeItem(x));
        setItems(data);
      }
    } catch (err) {
      alert("Error loading items");
    } finally {
      setLoading(false);
    }
  };

  // FORM INPUT CHANGE
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files?.length) {
      setForm((p) => ({ ...p, file: files[0] }));
    } else {
      setForm((p) => ({ ...p, [name]: value }));
    }
  };

  // SUBMIT (ADD/EDIT)
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.name || !form.category || !form.basePrice) {
      alert("Name, category, base price required");
      return;
    }

    try {
      setLoading(true);
      const fd = new FormData();

      Object.keys(form).forEach((k) => {
        if (form[k] !== "" && form[k] !== null) fd.append(k, form[k]);
      });

      let res;

      if (isCopyMode) {
        res = await axios.post(API_URL, fd);
      } else if (editId) {
        res = await axios.put(`${API_URL}/${editId}`, fd);
      } else {
        res = await axios.post(API_URL, fd);
      }

      if (res.data.success) {
        fetchItems();
        resetForm();
        setShowForm(false);
        setShowModal(false);
      }
    } catch (err) {
      alert("Save failed");
    } finally {
      setLoading(false);
    }
  };

  const resetForm = () => {
    setForm({
      name: "",
      category: "",
      subcategory: "",
      description: "",
      basePrice: "",
      profitLoss: "",
      validTill: "",
      file: null,
      status: "inactive",
    });
    setEditId(null);
    setIsCopyMode(false);
  };

  // EDIT ITEM
  const handleEdit = (item) => {
    setForm({
      name: item.name,
      category: item.category?._id || "",
      subcategory: item.subcategory?._id || "",
      description: item.description,
      basePrice: item.basePrice,
      profitLoss: item.profitLoss,
      validTill: item.validTill ? item.validTill.split("T")[0] : "",
      file: null,
      status: item.status,
    });

    setEditId(item._id);
    setIsCopyMode(false);
    setShowModal(true);
  };

  // COPY ITEM
  const handleCopyToEdit = (item) => {
    setForm({
      name: item.name,
      category: item.category?._id || "",
      subcategory: item.subcategory?._id || "",
      description: item.description,
      basePrice: item.basePrice,
      profitLoss: item.profitLoss,
      validTill: item.validTill ? item.validTill.split("T")[0] : "",
      file: null,
      status: item.status,
    });

    setEditId(null);
    setIsCopyMode(true);
    setShowModal(true);
  };

  // DELETE ONE
  const handleDelete = async (id) => {
    if (!window.confirm("Delete item?")) return;

    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchItems();
    } catch (err) {
      alert("Delete error");
    }
  };

  // STATUS TOGGLE
  const handleStatusToggle = async (item) => {
    try {
      const newStatus = item.status === "active" ? "inactive" : "active";
      await axios.put(`${API_URL}/status/${item._id}`, { status: newStatus });
      fetchItems();
    } catch (err) {
      alert("Status error");
    }
  };

  // QUICK DIFF ‚Üí update profitLoss
  
  // ------------------ CSV import/export ------------------
  const handleCsvSelect = (e) => {
    setCsvFile(e.target.files[0]);
  };

  const handleImportCsv = async () => {
    if (!csvFile) return alert("Select CSV file");
    try {
      const fd = new FormData();
      fd.append("file", csvFile);
      const res = await axios.post(`${API_URL}/import`, fd);
      if (res.data && res.data.success) {
        alert(`Imported ${res.data.inserted || 0} items`);
        setCsvFile(null);
        if (csvInputRef.current) csvInputRef.current.value = null;
        fetchItems();
      } else {
        alert("CSV import failed");
      }
    } catch (err) {
      console.error("CSV import error", err);
      alert("CSV import failed");
    }
  };

  const handleExportCsv = () => {
    if (!items.length) return alert("No items to export");

    const header = [
      "id",
      "name",
      "categoryName",
      "subcategoryName",
      "basePrice",
      "profitLoss",
      "salePrice",
      "lockedPrice",
      "yesterdayLock",
      "brokerDisplay",
      "status",
      "validTill",
      "description",
      "imageUrl",
    ];

    const rows = items.map((p) => [
      p._id,
      p.name || "",
      p.category?.name || "",
      p.subcategory?.name || "",
      p.basePrice ?? "",
      p.profitLoss ?? "",
      p.salePrice ?? "",
      p.lockedPrice ?? "",
      p.yesterdayLock ?? "",
      p.brokerDisplay ?? "",
      p.status || "",
      p.validTill ? new Date(p.validTill).toISOString().split("T")[0] : "",
      p.description || "",
      p.image || "",
    ]);

    const csvArray = [header, ...rows];
    const csvContent =
      "data:text/csv;charset=utf-8," +
      csvArray
        .map((r) => r.map((c) => `"${String(c).replace(/"/g, '""')}"`).join(","))
        .join("\n");

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.href = encodedUri;
    link.download = `all_prices_${Date.now()}.csv`;
    link.click();
  };

  const handleExportSelectedCsv = () => {
    if (!selectedItems.length) return alert("Select items to export");

    const selectedData = items.filter((x) => selectedItems.includes(x._id));
    const header = [
      "id",
      "name",
      "categoryName",
      "subcategoryName",
      "basePrice",
      "profitLoss",
      "salePrice",
      "lockedPrice",
      "yesterdayLock",
      "brokerDisplay",
      "status",
      "validTill",
      "description",
      "imageUrl",
    ];

    const rows = selectedData.map((p) => [
      p._id,
      p.name || "",
      p.category?.name || "",
      p.subcategory?.name || "",
      p.basePrice ?? "",
      p.profitLoss ?? "",
      p.salePrice ?? "",
      p.lockedPrice ?? "",
      p.yesterdayLock ?? "",
      p.brokerDisplay ?? "",
      p.status || "",
      p.validTill ? new Date(p.validTill).toISOString().split("T")[0] : "",
      p.description || "",
      p.image || "",
    ]);

    const csvArray = [header, ...rows];
    const csvContent =
      "data:text/csv;charset=utf-8," +
      csvArray.map((r) => r.map((c) => `"${String(c).replace(/"/g, '""')}"`).join(",")).join("\n");

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.href = encodedUri;
    link.download = `selected_prices_${Date.now()}.csv`;
    link.click();
  };

  // ------------------ Bulk delete / save ------------------
  const handleBulkDelete = async () => {
    if (!selectedItems.length) return alert("No items selected");
    if (!window.confirm("Delete selected items?")) return;

    try {
      await axios.post(`${API_URL}/delete-selected`, { ids: selectedItems });
      setSelectedItems([]);
      fetchItems();
      setBulkMode(false);
    } catch (err) {
      console.error("Bulk delete error", err);
      alert("Bulk delete failed");
    }
  };

  const handleBulkSave = async () => {
    if (!selectedItems.length) return alert("No items selected");

    // Prepare updates from local items state
    const updates = items
      .filter((x) => selectedItems.includes(x._id))
      .map((x) => ({
        id: x._id,
        basePrice: Number(x.basePrice),
        profitLoss: Number(x.profitLoss),
        status: x.status,
        validTill: x.validTill || null,
        name: x.name,
        category: x.category?._id || "",
        subcategory: x.subcategory?._id || "",
      }));

    try {
      const res = await axios.post(`${API_URL}/bulk-update`, { products: updates });
      if (res.data.success) {
        alert("Bulk Updated");
        setBulkMode(false);
        setSelectedItems([]);
        fetchItems();
      } else {
        alert("Bulk update failed");
      }
    } catch (err) {
      console.error("Bulk update error", err);
      alert("Bulk update failed");
    }
  };

  // ------------------ Filtering / Pagination ------------------
  const filteredItems = items.filter((item) => {
    const t = search.toLowerCase();
    const matchText =
      (item.name || "").toLowerCase().includes(t) ||
      (item.category?.name || "").toLowerCase().includes(t) ||
      (item.subcategory?.name || "").toLowerCase().includes(t);

    const matchCategory = !filterCategory || String(item.category?._id) === String(filterCategory);
    const matchSub =
      !filterSubcategory ||
      String(item.subcategory?._id || "") === String(filterSubcategory) ||
      String(item.subcategory?.name || "").toLowerCase() ===
        String(filterSubs.find((s) => s._id === filterSubcategory)?.name || "").toLowerCase();

    return matchText && matchCategory && matchSub;
  });

  const indexOfLast = currentPage * itemsPerPage;
  const currentItems = filteredItems.slice(indexOfLast - itemsPerPage, indexOfLast);
  const totalPages = Math.max(1, Math.ceil(filteredItems.length / itemsPerPage));

  const getFinalPrice = (item) => {
    // salePrice is authoritative
    return Number(item.salePrice ?? item.basePrice ?? 0);
  };

  // ------------------ Render ------------------
  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <h1 style={styles.title}>Product Management</h1>
      </div>

      <div style={{ display: "flex", gap: 10, alignItems: "center", marginBottom: 12 }}>
        <input
          type="text"
          placeholder="Search product, category or subcategory..."
          value={search}
          onChange={(e) => {
            setSearch(e.target.value);
            setCurrentPage(1);
          }}
          style={styles.searchInput}
        />

        <button
          style={styles.addButton}
          onClick={() => {
            setShowForm((v) => !v);
            resetForm();
            setShowModal(false);
          }}
        >
          {showForm ? "‚úñ Close" : "‚ûï Add Product"}
        </button>

        <div style={{ marginLeft: "auto", display: "flex", gap: 8 }}>
          <input ref={csvInputRef} type="file" accept=".csv" onChange={handleCsvSelect} />
          <button style={styles.csvBtn} onClick={handleImportCsv}>Import CSV</button>
          <button style={styles.csvBtn} onClick={handleExportSelectedCsv}>Export Selected</button>
          <button style={styles.csvBtnPrimary} onClick={handleExportCsv}>Export All</button>
        </div>
      </div>

      {selectedItems.length > 0 && (
        <div style={styles.bulkBar}>
          {!bulkMode ? (
            <div style={{ display: "flex", gap: 8 }}>
              <button style={styles.btnDelete} onClick={handleBulkDelete}>üóë Bulk Delete</button>
              <button style={styles.btnPrimary} onClick={() => setBulkMode(true)}>‚úè Bulk Edit</button>
            </div>
          ) : (
            <div style={styles.bulkPanel}>
              <h3 style={styles.bulkPanelTitle}>‚úè Bulk Edit Selected Items</h3>
              {items
                .filter((item) => selectedItems.includes(item._id))
                .map((item) => (
                  <div key={item._id} style={styles.bulkItemBox}>
                    <h4 style={styles.bulkItemTitle}>{item.name}</h4>
                    <div style={styles.formGrid}>
                      <div style={styles.formGroup}>
                        <label style={styles.label}>Name</label>
                        <input value={item.name} onChange={(e) => updateLocalItemField(item._id, "name", e.target.value)} style={styles.input} />
                      </div>

                      <div style={styles.formGroup}>
                        <label style={styles.label}>Base Price</label>
                        <input type="number" value={item.basePrice} onChange={(e) => updateLocalItemField(item._id, "basePrice", Number(e.target.value))} style={styles.input} />
                      </div>

                      <div style={styles.formGroup}>
                        <label style={styles.label}>Profit/Loss</label>
                        <input type="number" value={item.profitLoss} onChange={(e) => updateLocalItemField(item._id, "profitLoss", Number(e.target.value))} style={styles.input} />
                      </div>

                      <div style={styles.formGroup}>
                        <label style={styles.label}>Status</label>
                        <select value={item.status} onChange={(e) => updateLocalItemField(item._id, "status", e.target.value)} style={styles.select}>
                          <option value="active">active</option>
                          <option value="inactive">inactive</option>
                        </select>
                      </div>
                    </div>
                  </div>
                ))}

              <div style={{ display: "flex", gap: 10, marginTop: 12 }}>
                <button style={styles.btnPrimary} onClick={handleBulkSave}>‚úî Save All</button>
                <button style={styles.btnCancel} onClick={() => setBulkMode(false)}>‚úñ Cancel</button>
              </div>
            </div>
          )}
        </div>
      )}

      {showForm && (
        <div style={styles.formCard}>
          <h2 style={styles.formTitle}> Add Product</h2>

          <form onSubmit={handleSubmit}>
            <div style={styles.formGrid}>
              <div style={styles.formGroup}>
                <label style={styles.label}>Product Name *</label>
                <input required name="name" value={form.name} onChange={handleChange} style={styles.input} />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Category *</label>
                <select required name="category" value={form.category} onChange={(e) => {
                  handleChange(e);
                  setForm((p) => ({ ...p, subcategory: "" }));
                }} style={styles.select}>
                  <option value="">Select Category</option>
                  {categories.map((c) => (<option value={c._id} key={c._id}>{c.name}</option>))}
                </select>
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Subcategory</label>
                <select name="subcategory" value={form.subcategory} onChange={handleChange} disabled={!subcategories.length} style={styles.select}>
                  <option value="">Select Subcategory</option>
                  {subcategories.map((s) => (<option value={s._id} key={s._id}>{s.name}</option>))}
                </select>
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Base Price *</label>
                <input type="number" required name="basePrice" value={form.basePrice} onChange={handleChange} style={styles.input} />
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Profit / Loss</label>
                <input type="number" name="profitLoss" value={form.profitLoss} onChange={handleChange} style={styles.input} />
              </div>

              

              <div style={styles.formGroup}>
                <label style={styles.label}>Image</label>
                <input type="file" accept="image/*" onChange={handleChange} style={styles.fileInput} />
              </div>
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Description</label>
              <textarea name="description" value={form.description} onChange={handleChange} style={styles.textarea}></textarea>
            </div>

            <div style={styles.formActions}>
              <button style={styles.btnPrimary} disabled={loading}>{loading ? "Saving..." : editId ? (isCopyMode ? "Save Copy" : "Update Product") : "Add Product"}</button>

              <button type="button" style={styles.btnCancel} onClick={() => { setShowForm(false); resetForm(); }}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      <div style={styles.tableCard}>
        <div style={styles.tableHeader}>
          <h2 style={styles.tableTitle}>Items</h2>
          <span style={styles.totalCount}>Total: {filteredItems.length}</span>
        </div>

        <div style={styles.tableWrapper}>
          <table style={styles.table}>
            <thead>
              <tr>
                <th style={styles.th}>
                  <input
                    type="checkbox"
                    checked={selectedItems.length === filteredItems.length && filteredItems.length > 0}
                    onChange={() => {
                      if (selectedItems.length === filteredItems.length) setSelectedItems([]);
                      else setSelectedItems(filteredItems.map((x) => x._id));
                    }}
                  />
                </th>
                <th style={styles.th}>Sr</th>
                <th style={styles.th}>Image</th>
                <th style={styles.th}>Name</th>
                <th style={styles.th}>Category</th>
                <th style={styles.th}>Subcategory</th>

                <th style={styles.th}>Base</th>
                <th style={styles.th}>Profit / Loss</th>
                <th style={styles.th}>Sale Price</th>
                <th style={styles.th}>Locked Price</th>
                
                <th style={styles.th}>Tejji/Maddi</th>

                
                <th style={styles.th}>Status</th>
                
                <th style={styles.th}>Actions</th>
              </tr>
            </thead>

            <tbody>
              {currentItems.map((item, i) => (
                <tr key={item._id} style={styles.tr}>
                  <td style={styles.td}>
                    <input
                      type="checkbox"
                      checked={selectedItems.includes(item._id)}
                      onChange={() =>
                        setSelectedItems((prev) => (prev.includes(item._id) ? prev.filter((x) => x !== item._id) : [...prev, item._id]))
                      }
                    />
                  </td>

                  <td style={styles.td}>{(currentPage - 1) * itemsPerPage + (i + 1)}</td>

                  <td style={styles.td}>{item.image ? <img src={item.image} style={styles.tableImg} alt="" /> : "No Img"}</td>

                  <td style={styles.td}>{item.name}</td>
                  <td style={styles.td}>{item.category?.name || "-"}</td>
                  <td style={styles.td}>{item.subcategory?.name || "-"}</td>

                  <td style={styles.td}>
                    <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
                      <input
                        type="number"
                        value={quickEdit[item._id] !== undefined ? quickEdit[item._id] : item.basePrice}
                        onChange={(e) => setQuickEdit((p) => ({ ...p, [item._id]: e.target.value }))}
                        style={{ ...styles.input, padding: "6px 8px", width: 90 }}
                      />
                      <button style={styles.btnSmall} onClick={() => applyQuickBase(item)}>‚úî</button>
                    </div>
                    <small style={{ display: "block", color: "#2b8d28", marginTop: 4 }}>Saved: ‚Çπ{item.basePrice}</small>
                  </td>

                  <td style={styles.td}>
                    <input
                      type="number"
                      value={item.profitLoss}
                      onChange={(e) => updateLocalItemField(item._id, "profitLoss", Number(e.target.value))}
                      style={{ ...styles.input, width: 90 }}
                    />
                    <small style={{ display: "block", color: "#666" }}>Sale will recalc on save</small>
                  </td>

                  <td style={styles.td}>‚Çπ{getFinalPrice(item).toFixed(2)}</td>
                  <td style={styles.td}>‚Çπ{item.lockedPrice?.toFixed(2)}</td>
                  <td style={styles.td}>{item.brokerDisplay !== undefined ? item.brokerDisplay.toFixed(2) : "0.00"}</td>

                

                  <td style={styles.td}>
                    <button style={item.status === "active" ? styles.statusActive : styles.statusInactive} onClick={() => handleStatusToggle(item)}>
                      {item.status === "active" ? "Active" : "Inactive"}
                    </button>
                  </td>

                

                  <td style={styles.td}>
                    <div style={{ display: "flex", gap: 6 }}>
                      <button style={styles.btnSmall} onClick={() => handleEdit(item)}>‚úè Edit</button>
                      <button style={styles.btnSmall} onClick={() => handleCopyToEdit(item)}>üìÑ Copy</button>
                      <button style={styles.btnDelete} onClick={() => handleDelete(item._id)}>üóë Delete</button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div style={styles.pagination}>
          <button style={styles.paginationBtn} disabled={currentPage === 1} onClick={() => setCurrentPage((p) => p - 1)}>Previous</button>

          {Array.from({ length: totalPages }, (_, i) => (
            <button key={i} style={i + 1 === currentPage ? styles.paginationBtnActive : styles.paginationBtn} onClick={() => setCurrentPage(i + 1)}>{i + 1}</button>
          ))}

          <button style={styles.paginationBtn} disabled={currentPage === totalPages} onClick={() => setCurrentPage((p) => p + 1)}>Next</button>
        </div>
      </div>
    </div>
  );
}

/* ---------------------- Helper: updateLocalItemField ---------------------- */
function updateLocalItemField(id, key, value) {
  // This function will be hoisted to window scope if defined outside; to keep inside component,
  // we defined it by referencing state updater in part1. But since we are sending single-file component,
  // ensure this helper exists in the top of the file. If you paste both parts in same file, replace this with
  // the inline version used earlier:
  // setItems(prev => prev.map(x => x._id === id ? { ...x, [key]: value } : x));
  // For safety, we attach to window to fallback:
  if (typeof window.__updateLocal === "function") {
    window.__updateLocal(id, key, value);
  } else {
    console.warn("updateLocalItemField helper missing ‚Äî please ensure you paste PART 1 then PART 2 in same file so the internal updater is available.");
  }
}

