  tableTitle: {
    fontSize: "21px",
    fontWeight: "700",
    color: "#0d3b66",
  },
  totalCount: {
    fontSize: "15px",
    color: "#6c757d",
    fontWeight: "600",
  },
  tableWrapper: {
    overflowX: "auto",
  },
  table: {
    width: "100%",
    borderCollapse: "collapse",
    fontSize: "14px",
  },
  th: {
    background: "#e9f2ff",
    padding: "12px",
    fontWeight: "700",
    color: "#0d3b66",
    textAlign: "left",
    whiteSpace: "nowrap",
  },
  tr: {
    borderBottom: "1px solid #edf2f7",
  },
  td: {
    padding: "12px 10px",
    whiteSpace: "nowrap",
  },
  tableImg: {
    width: "55px",
    height: "55px",
    borderRadius: "8px",
    objectFit: "cover",
    border: "1px solid #cfd9e5",
  },
  statusActive: {
    background: "#93d8ba",
    padding: "7px 12px",
    borderRadius: "8px",
    color: "black",
    fontWeight: "600",
    border: "none",
    cursor: "pointer",
  },
  statusInactive: {
    background: "#d9b1b1",
    padding: "7px 12px",
    borderRadius: "8px",
    color: "black",
    fontWeight: "600",
    border: "none",
    cursor: "pointer",
  },
  menuButton: {
    background: "#f0f0f0",
    border: "none",
    borderRadius: "6px",
    padding: "6px 12px",
    fontSize: "20px",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "0.2s ease",
  },
  dropdown: {
    position: "absolute",
    right: "0",
    top: "35px",
    background: "#fff",
    border: "1px solid #ddd",
    borderRadius: "8px",
    boxShadow: "0 4px 12px rgba(0,0,0,0.15)",
    zIndex: "100",
    minWidth: "140px",
  },
  dropdownItem: {
    display: "block",
    width: "100%",
    padding: "10px 15px",
    background: "transparent",
    border: "none",
    textAlign: "left",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    transition: "0.2s ease",
    borderBottom: "1px solid #f0f0f0",
  },
  dropdownItemDelete: {
    display: "block",
    width: "100%",
    padding: "10px 15px",
    background: "transparent",
    border: "none",
    textAlign: "left",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "500",
    color: "#e63946",
    transition: "0.2s ease",
  },
  pagination: {
    marginTop: "18px",
    display: "flex",
    justifyContent: "center",
    gap: "10px",
    flexWrap: "wrap",
  },
  paginationBtn: {
    padding: "9px 13px",
    borderRadius: "8px",
    background: "#fff",
    border: "1px solid #ddd",
    cursor: "pointer",
    fontSize: "14px",
  },
  paginationBtnActive: {
    padding: "9px 13px",
    borderRadius: "8px",
    background: "#007bff",
    color: "white",
    border: "1px solid #0062cc",
    cursor: "pointer",
    fontSize: "14px",
  },
  modalOverlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    background: "rgba(0, 0, 0, 0.5)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: "1000",
    padding: "20px",
  },
  modal: {
    background: "#fff",
    borderRadius: "16px",
    padding: "30px",
    maxWidth: "700px",
    width: "100%",
    maxHeight: "90vh",
    overflowY: "auto",
    boxShadow: "0 10px 40px rgba(0, 0, 0, 0.3)",
  },
  modalTitle: {
    fontSize: "22px",
    fontWeight: "700",
    color: "#0d3b66",
    marginBottom: "20px",
    textAlign: "center",
  },
  
  
}
