  // BULK
  const [selectedItems, setSelectedItems] = useState([]);
  const [bulkMode, setBulkMode] = useState(false);

  const csvInputRef = useRef();

  // PAGINATION
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 30;

  // ----------------- FETCH ITEMS -----------------
  useEffect(() => {
    fetchItems();
    fetchCategories();

    const handleResize = () => setIsMobile(window.innerWidth < 768);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const res = await axios.get(API_URL);
      if (res.data.success) {
        setItems(res.data.data);
      }
    } catch (err) {
      console.log("Fetch error", err);
    } finally {
      setLoading(false);
    }
  };

  const fetchCategories = async () => {
    try {
      const res = await axios.get(CATEGORY_URL);
      if (res.data.success) {
        setCategories(res.data.categories);
      }
    } catch (err) {
      console.log("Cat fetch err", err);
    }
  };

  // ----------------- UPDATE SUBCATEGORY LIST WHEN CATEGORY CHANGES -----------------
  useEffect(() => {
    if (!form.category) {
      setSubcategories([]);
      setForm((prev) => ({ ...prev, subcategory: "" }));
      return;
    }

    const categoryObj = categories.find((c) => c._id === form.category);
    const subs = categoryObj?.subcategories || [];
    setSubcategories(subs);

    const stillExists = subs.some((s) => s._id === form.subcategory);
    if (!stillExists) {
      setForm((prev) => ({ ...prev, subcategory: "" }));
    }
  }, [form.category, categories]);

  // ----------------- CREATE CATEGORY -----------------
  const handleCreateCategory = async () => {
    if (!newCategoryName) return alert("Enter category name");

    try {
      const fd = new FormData();
      fd.append("name", newCategoryName);
      if (newCategoryImage) fd.append("image", newCategoryImage);

      const res = await axios.post(CATEGORY_URL, fd);

      if (res.data.success) {
        alert("Category Created!");
        setNewCategoryName("");
        setNewCategoryImage(null);
        setNewCategoryPreview(null);
        setAddingCategory(false);
        fetchCategories();
      }
    } catch (err) {
      alert("Category creation failed");
    }
  };

  // ----------------- CREATE SUBCATEGORY -----------------
  const handleCreateSub = async () => {
    if (!newSubName) return alert("Enter subcategory name");
    if (!form.category) return alert("Select category");

    try {
      const fd = new FormData();
      fd.append("name", newSubName);
      if (newSubImage) fd.append("image", newSubImage);

      const res = await axios.post(`${CATEGORY_URL}/${form.category}/sub`, fd);
      if (res.data.success) {
        alert("Subcategory created!");

        setNewSubName("");
        setNewSubImage(null);
        setNewSubPreview(null);
        setAddingSub(false);

        fetchCategories();
      }
    } catch (err) {
      alert("Subcategory create failed");
    }
  };

  // ----------------- HANDLE INPUT -----------------
  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (files) {
      setForm({ ...form, file: files[0] });
    } else {
      setForm({ ...form, [name]: value });
    }
  };

  // ----------------- SUBMIT FORM (ADD/UPDATE) -----------------
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      if (!form.name || !form.category || !form.basePrice) {
        alert("Missing required fields");
        setLoading(false);
        return;
      }

      const fd = new FormData();
      Object.entries(form).forEach(([key, val]) => {
        if (val) fd.append(key, val);
      });

      if (isCopyMode) {
        await axios.post(API_URL, fd);
        alert("Copied as new");
      } else if (editId) {
        await axios.put(`${API_URL}/${editId}`, fd);
      } else {
        await axios.post(API_URL, fd);
      }

      fetchItems();

      setForm({
        name: "",
        category: "",
        subcategory: "",
        description: "",
        basePrice: "",
        difference: "",
        validTill: "",
        file: null,
      });

      setEditId(null);
      setIsCopyMode(false);
    } catch (err) {
      alert("Save failed");
    } finally {
      setLoading(false);
    }
  };

  // ----------------- EDIT PRODUCT -----------------
  const handleEdit = (item) => {
    setForm({
      name: item.name,
      category: item.category?._id || "",
      subcategory: item.subcategory?._id || "",
      description: item.description || "",
      basePrice: item.basePrice,
      difference: item.difference,
      validTill: item.validTill ? item.validTill.split("T")[0] : "",
      file: null,
    });

    const cat = categories.find((c) => c._id === item.category?._id);
    setSubcategories(cat?.subcategories || []);

    setEditId(item._id);
    setIsCopyMode(false);

    window.scrollTo({ top: 0, behavior: "smooth" });
  };
  // ----------------- COPY -----------------
  const handleCopyToEdit = (item) => {
    setForm({
      name: item.name,
      category: item.category?._id || "",
      subcategory: item.subcategory?._id || "",
      description: item.description || "",
      basePrice: item.basePrice,
      difference: item.difference,
      validTill: item.validTill ? item.validTill.split("T")[0] : "",
      file: null,
    });

    const cat = categories.find((c) => c._id === item.category?._id);
    setSubcategories(cat?.subcategories || []);

    setEditId(null);
    setIsCopyMode(true);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  // ----------------- DELETE -----------------
  const handleDelete = async (id) => {
    if (!window.confirm("Delete?")) return;
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchItems();
    } catch (err) {
      alert("Delete failed");
    }
  };

  // ----------------- STATUS TOGGLE -----------------
  const handleStatusToggle = async (item) => {
    const newStatus = item.status === "active" ? "inactive" : "active";
    try {
      await axios.put(`${API_URL}/status/${item._id}`, { status: newStatus });
      setItems((prev) =>
        prev.map((x) =>
          x._id === item._id ? { ...x, status: newStatus } : x
        )
      );
    } catch (err) {
      alert("Status update failed");
    }
  };

  // ----------------- EXPORT SELECTED CSV -----------------
  const handleExportSelectedCsv = () => {
    if (selectedItems.length === 0) {
      alert("Select at least one item");
      return;
    }

    const selectedData = items.filter((x) => selectedItems.includes(x._id));

    const rows = [
      [
        "name",
        "categoryName",
        "subcategoryName",
        "basePrice",
        "difference",
        "finalPrice",
        "status",
        "validTill",
        "description",
        "imageUrl",
      ],
      ...selectedData.map((p) => [
        p.name,
        p.category?.name || "",
        p.subcategory?.name || "",
        p.basePrice,
        p.difference,
        p.basePrice + (p.difference || 0),
        p.status,
        p.validTill ? new Date(p.validTill).toISOString().split("T")[0] : "",
        p.description || "",
        p.image || "",
      ]),
    ];

    const csvString =
      "data:text/csv;charset=utf-8," +
      rows.map((r) => r.join(",")).join("\n");

    const link = document.createElement("a");
    link.href = csvString;
    link.download = "selected_prices.csv";
    link.click();
  };

  // ----------------- SEARCH FILTER -----------------
  const filteredItems = items.filter((item) => {
    const t = search.toLowerCase();
    return (
      (item.name || "").toLowerCase().includes(t) ||
      (item.category?.name || "").toLowerCase().includes(t) ||
      (item.subcategory?.name || "").toLowerCase().includes(t)
    );
  });

  // ----------------- PAGINATION -----------------
  const indexOfLast = currentPage * itemsPerPage;
  const currentItems = filteredItems.slice(
    indexOfLast - itemsPerPage,
    indexOfLast
  );
  const totalPages = Math.ceil(filteredItems.length / itemsPerPage);

  return (
    <div className="price-container">
      {/* TABLE SECTION */}
      <div className="table-card">
        <div className="table-header">
          <h2>üìã Items</h2>
          <span>Total: {filteredItems.length}</span>
        </div>

        {!isMobile ? (
          <table>
            <thead>
              <tr>
                <th>
                  <input
                    type="checkbox"
                    checked={
                      selectedItems.length === filteredItems.length &&
                      filteredItems.length > 0
                    }
                    onChange={() => {
                      if (selectedItems.length === filteredItems.length)
                        setSelectedItems([]);
                      else
                        setSelectedItems(filteredItems.map((x) => x._id));
                    }}
                  />
                </th>
                <th>Sr</th>
                <th>Image</th>
                <th>Name</th>
                <th>Category</th>
                <th>Subcategory</th>
                <th>Base</th>
                <th>Diff</th>
                <th>Final</th>
                <th>Status</th>
                <th>Valid Till</th>
                <th>Actions</th>
              </tr>
            </thead>

            <tbody>
              {currentItems.map((item, i) => (
                <tr key={item._id}>
                  <td>
                    <input
                      type="checkbox"
                      checked={selectedItems.includes(item._id)}
                      onChange={() =>
                        selectedItems.includes(item._id)
                          ? setSelectedItems(
                              selectedItems.filter((x) => x !== item._id)
                            )
                          : setSelectedItems([...selectedItems, item._id])
                      }
                    />
                  </td>

                  <td>{(currentPage - 1) * itemsPerPage + (i + 1)}</td>

                  <td>
                    {item.image ? <img src={item.image} alt="" /> : "No Img"}
                  </td>

                  <td>{item.name}</td>

                  <td>{item.category?.name || "-"}</td>

                  {/* ‚≠ê‚≠ê SUBCATEGORY ALWAYS SHOWS NOW ‚≠ê‚≠ê */}
                  <td>{item.subcategory?.name || "-"}</td>

                  <td>‚Çπ{item.basePrice}</td>

                  <td>{item.difference || 0}</td>

                  <td>
                    ‚Çπ{Number(item.basePrice) + Number(item.difference || 0)}
                  </td>

                  <td>
                    <button
                      className={
                        item.status === "active"
                          ? "status-active"
                          : "status-inactive"
                      }
                      onClick={() => handleStatusToggle(item)}
                    >
                      {item.status === "active"
                        ? "üü¢ Active"
                        : "üî¥ Inactive"}
                    </button>
                  </td>

                  <td>
                    {item.validTill
                      ? new Date(item.validTill).toLocaleDateString()
                      : "-"}
                  </td>

                  <td>
                    <button className="btn edit" onClick={() => handleEdit(item)}>
                      Edit
                    </button>

                    <button
                      className="btn delete"
                      onClick={() => handleDelete(item._id)}
                    >
                      Delete
                    </button>

                    <button
                      className="btn small"
                      style={{ background: "#6f42c1", color: "white" }}
                      onClick={() => handleCopyToEdit(item)}
                    >
                      Copy
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          // MOBILE VIEW
          <div className="mobile-list">
            {currentItems.map((item) => (
              <div key={item._id} className="mobile-card">
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item._id)}
                  onChange={() =>
                    selectedItems.includes(item._id)
                      ? setSelectedItems(
                          selectedItems.filter((x) => x !== item._id)
                        )
                      : setSelectedItems([...selectedItems, item._id])
                  }
                />

                {item.image ? (
                  <img src={item.image} alt="" />
                ) : (
                  <div>No Image</div>
                )}

                <h3>{item.name}</h3>

                <p>
                  <b>Category:</b> {item.category?.name}
                </p>

                <p>
                  <b>Subcategory:</b> {item.subcategory?.name || "-"}
                </p>

                <p>
                  <b>Base:</b> ‚Çπ{item.basePrice}
                </p>

                <p>
                  <b>Final:</b> ‚Çπ
                  {Number(item.basePrice) + Number(item.difference || 0)}
                </p>

                <button
                  className={
                    item.status === "active"
                      ? "status-active"
                      : "status-inactive"
                  }
                  onClick={() => handleStatusToggle(item)}
                >
                  {item.status === "active" ? "Active" : "Inactive"}
                </button>

                <div className="actions">
                  <button className="btn edit" onClick={() => handleEdit(item)}>
                    Edit
                  </button>

                  <button
                    className="btn delete"
                    onClick={() => handleDelete(item._id)}
                  >
                    Delete
                  </button>

                  <button
                    className="btn small"
                    style={{ background: "#6f42c1", color: "#fff" }}
                    onClick={() => handleCopyToEdit(item)}
                  >
                    Copy
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* PAGINATION */}
        <div className="pagination">
          <button
            disabled={currentPage === 1}
            onClick={() => setCurrentPage((p) => p - 1)}
          >
            Previous
          </button>

          {Array.from({ length: totalPages }, (_, i) => (
            <button
              key={i}
              className={i + 1 === currentPage ? "active-page" : ""}
              onClick={() => setCurrentPage(i + 1)}
            >
              {i + 1}
            </button>
          ))}

          <button
            disabled={currentPage === totalPages}
            onClick={() => setCurrentPage((p) => p + 1)}
          >
            Next
          </button>
        </div>
      </div>
    </div>
  );
}
