// export default CategoryManager;
import React, { useState, useEffect, useRef } from "react";
import axios from "axios";
import "./CategoryManager.css";

const API = "https://grocerrybackend.vercel.app/api/categories";

export default function CategoryManager() {
  const [categories, setCategories] = useState([]);
  const [formOpen, setFormOpen] = useState(false);
  const [catForm, setCatForm] = useState({ name: "", image: null });
  const [editId, setEditId] = useState(null);
  const [openMenu, setOpenMenu] = useState(null);
  const menuRef = useRef();

  // CLICK OUTSIDE CLOSE MENU
  useEffect(() => {
    const handler = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setOpenMenu(null);
      }
    };
    document.addEventListener("mousedown", handler);
    return () => document.removeEventListener("mousedown", handler);
  }, []);

  // FETCH CATEGORIES
  const fetchCategories = async () => {
    const res = await axios.get(API);
    setCategories(res.data.categories || []);
  };
  useEffect(() => {
    fetchCategories();
  }, []);

  // INPUT CHANGE
  const handleChange = (e) => {
    if (e.target.files) {
      setCatForm({ ...catForm, image: e.target.files[0] });
    } else {
      setCatForm({ ...catForm, [e.target.name]: e.target.value });
    }
  };

  // SAVE CATEGORY
  const saveCategory = async (e) => {
    e.preventDefault();
    const fd = new FormData();
    fd.append("name", catForm.name);
    if (catForm.image) fd.append("image", catForm.image);

    if (editId) {
      await axios.put(`${API}/${editId}`, fd);
    } else {
      await axios.post(API, fd);
    }

    setCatForm({ name: "", image: null });
    setEditId(null);
    setFormOpen(false);
    fetchCategories();
  };

  // DELETE
  const deleteCategory = async (id) => {
    if (!window.confirm("Delete?")) return;
    await axios.delete(`${API}/${id}`);
    fetchCategories();
  };

  return (
    <div className="cat-container">
      <div className="cat-header">
        <h2>ðŸ“‚ Category Manager</h2>
      </div>

      {/* Add Button */}
      <button className="add-btn" onClick={() => setFormOpen(!formOpen)}>
        {formOpen ? "Close Form" : "Add Category +"}
      </button>

      {/* Category Form */}
      {formOpen && (
        <form className="cat-form" onSubmit={saveCategory}>
          <input
            name="name"
            type="text"
            placeholder="Category name"
            value={catForm.name}
            onChange={handleChange}
          />

          <input name="image" type="file" onChange={handleChange} />

          <button type="submit">{editId ? "Update" : "Add"}</button>
        </form>
      )}

      {/* -------- DESKTOP TABLE -------- */}
      <div className="cat-table-wrapper">
        <table className="cat-table">
          <thead>
            <tr>
              <th>#</th>
              <th>Img</th>
              <th>Name</th>
              <th>Subs</th>
              <th>...</th>
            </tr>
          </thead>

          <tbody>
            {categories.map((cat, i) => (
              <tr key={cat._id}>
                <td>{i + 1}</td>

                <td>
                  <img src={cat.image} className="cat-img" alt="" />
                </td>

                <td>{cat.name}</td>

                <td>{cat.subcategories?.length}</td>

                {/* 3 Dots */}
                <td className="act-td" ref={menuRef}>
                  <button
                    className="dots-btn"
                    onClick={() =>
                      setOpenMenu(openMenu === cat._id ? null : cat._id)
                    }
                  >
                    â‹®
                  </button>

                  {openMenu === cat._id && (
                    <div className="dots-menu">
                      <button
                        onClick={() => {
                          setEditId(cat._id);
                          setCatForm({ name: cat.name });
                          setFormOpen(true);
                          setOpenMenu(null);
                        }}
                      >
                        Edit
                      </button>

                      <button onClick={() => deleteCategory(cat._id)}>
                        Delete
                      </button>

                      <button onClick={() => alert("Manage subs UI")}>
                        Manage
                      </button>
                    </div>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* -------- MOBILE HORIZONTAL LIST -------- */}
      <div className="mobile-list">
        {categories.map((cat) => (
          <div className="mobile-item" key={cat._id}>
            <img src={cat.image} className="mobile-thumb" />
            <p>{cat.name}</p>
            <span>{cat.subcategories?.length} subs</span>
          </div>
        ))}
      </div>
    </div>
  );
}
